// Qui riprendiamo i valori dal nodo precedente.
let device = msg.payload.devInstance;
let sensor = msg.payload.sensorType;
let value = msg.payload.value;


/* Qui andiamo a recuperare le soglie stabilite per il
 * nostro sensore
 */
let minValue = global.get("brewer_"+device+"_minTemp");
let maxValue = global.get("brewer_"+device+"_maxTemp");

if(minValue === undefined){
    minValue = 16;
}
if(maxValue === undefined){
    maxValue = 26;
}
let avg = (minValue + maxValue)/2;


/* Recuperiamo i valori della cintura riscaldante e creiamo
 * il msg per il controllo della stessa. L'ID del WiFi relay
 * correlato alla scheda ESP è uguale all'ID della scheda 
 * ESP + 1.
 */
let beltDevice = device+1;
let beltVariable = "brewer_"+beltDevice+"_belt";
let beltStatus = flow.get(beltVariable);
if(beltStatus === undefined){
    beltStatus = false;
    flow.set(beltVariable, beltStatus);
}
let beltControl={
    topic: "cmd/br/"+beltDevice+"/3306/0/5850",
    payload: ""
};


/* Qui invece gestiamo i valori del led d'allarme */
let ledVariable = "brewer_"+device+"_tempAlarm";
let ledStatus = flow.get(ledVariable);
if(ledStatus === undefined){
    ledStatus = false;
    flow.set(ledVariable, ledStatus);
}
let ledControl={
    topic: "cmd/br/"+device+"/3311/0/5850",
    payload: ""
};

if(value<minValue){
    /* Abbiamo rilevato una temperatura che è minore di
     * quella minima accettabile, quindi dobbiamo far partire
     * la cintura riscaldante (a meno che non sia già partita)
     */
    if(!beltStatus){
        beltControl.payload = {
            value: "ON"
        };
    }
    else{
        // La cintura è già attiva.
        beltControl = null;
    }
    if(!ledStatus){
        ledControl.payload = {
            v: "ON"
        };
        flow.set("brewer_"+device+"_tempAlarm",true);
    }
    else ledControl = null;

}
else if(value>maxValue){
    /* La cisterna è troppo calda. Dobbiamo far 
     * partire l'allarme
     */
    beltControl = null;
    if(!ledStatus){
        // L'allarme non è attivo
        ledControl.payload = {
            v: "ON"
        };
        flow.set("brewer_"+device+"_tempAlarm",true);
    }
    else ledControl = null;
}
else if(value>=minValue && value < maxValue){
    /* Se siamo qui, la temperatura é a posto. */
    if(value>=avg){
        /* La temperatura ha superato la media di valori 
         * ammessi. Se c'era una cintura accesa in precedenza
         * ora è il momento di spegnerla
         */
        if(beltStatus){
            beltControl.payload = {
                value: "OFF"
            };
        }
        else beltControl = null;
    }
    if(ledStatus){
        /* Se c'era un allarme attivo, ora è il momento di
           spegnerlo */
        ledControl.payload = {
            v: "OFF",
        };
        flow.set("brewer_"+device+"_tempAlarm",false)
    }
    else ledControl = null;
}

return [beltControl,ledControl];

