let value=msg.payload.value;
let dev = msg.payload.dev;
let obj = msg.payload.objInstance;
let timestamp = msg.payload.timestamp;


//Qui recuperiamo il precedente valore d'allarme
const tempAlarm="stocker_"+dev+"_temp_"+obj;
let alarm = flow.get(tempAlarm);

//Questa è la soglia definita nel Recipy MGMT
let maxTemp=global.get("stocker_max_temp");
let minTemp=global.get("stocker_min_temp");
/* se non c'è alcuna customizzazione sulla soglia di
     * umidità, allora impostiamo il valore di default.
     */
if(maxTemp === undefined){
    maxTemp = 26;
}
if(minTemp === undefined){
    minTemp = 4;
}
/* Questo if viene eseguito solo la prima volta, per
 * inizializzare la variable */
if(alarm === undefined){
    alarm = false;
    flow.set(tempAlarm, false);
}

let mqttMessage = {
    topic: "cmd/st/"+dev+"/3311/0/5850",
    payload: ""
};

if(((value < maxTemp) && (value > minTemp)) && alarm){
    // temperatura a posto, ma c'è un allarme attivo
    mqttMessage.payload = {"v":"OFF"};
    flow.set(tempAlarm, false);
}
else if((value >= maxTemp) && !alarm){
    // alta temperatura rilevata, ma nessun allarme attivo
    mqttMessage.payload = {"v":"ON"};
    flow.set(tempAlarm, true);
}
else if((value <= minTemp) && !alarm){
    // bassa temperatura rilevata, ma nessun allarme attivo
    mqttMessage.payload = {"v":"ON"};
    flow.set(tempAlarm, true);
}
else{
    // In questo caso non dobbiamo fare nula
    mqttMessage = null;
}

return [msg, mqttMessage];

