    // Fuoco rilevato, ma allarme spento
    flow.set(fireVariable, true);
    msg.payload={"v":"ON"};
}
else if(!result && alarm){
    // Fuoco non rilevato, ma allarme attivo
    flow.set(fireVariable, false);
    msg.payload={"v":"OFF"};
}
else{
